        private async void Open_Click(object sender, RoutedEventArgs e)
        {
            var fileOpenPicker = new FileOpenPicker();
            fileOpenPicker.SuggestedStartLocation = PickerLocationId.ComputerFolder;
            fileOpenPicker.FileTypeFilter.Add(".SRHChar");
            StorageFile pickedFile = await fileOpenPicker.PickSingleFileAsync();

            if (pickedFile != null)
            {
                if (!pickedFile.Name.EndsWith(".SRHChar"))
                {
                    Log.Write("Wrong Filetype", TLIB.LogType.Error);
                }
                else
                {
                    try
                    {
                        // File was picked, you can now use it
                        string fileContent = await FileIO.ReadTextAsync(pickedFile);
                        Log.Write("Read " + fileContent.Length + "bytes");
                        try
                        {
                            MainObject = CharHolderIO.Deserialize(fileContent);
                            Log.Write("File Loaded");
                        }
                        catch (Exception ex)
                        {
                            Log.Write("Could not load char", ex);
                        }
                    }
                    catch (Exception ex)
                    {
                        Log.Write("Could not read file", ex);
                    }
                }
            }
            else
            {
                // No file was picked or the dialog was cancelled.
            }
        }
